import React, { useState } from "react";
import CritRoll from "./CritRoll";
import CritFail from "./CritFail";

const Roll = (props) => {
  const { amount, sides, modifier, rolls } = props;

  const addModifier = (roll, modifier) => {
    return roll + modifier;
  };

  const determineDisplay = () => {
    // const rolls = [];
    let stringBlock = amount + "d" + sides + ": ";

    return rolls.map((roll, i) => {
      // if this is the last roll, don't add a space
      if (i === rolls.length - 1) {
        if (roll === 20) {
          console.log(roll);
          return <CritRoll roll={roll} />;
        } else if (roll === 1) {
          return <CritFail roll={roll} />;
        } else {
          return roll;
        }
        // else add a space
      } else {
        if (roll === 20) {
          console.log(roll);
          return (
            <>
              <CritRoll roll={roll} /> +{" "}
            </>
          );
        } else if (roll == 1) {
          return (
            <>
              <CritFail roll={roll} /> +{" "}
            </>
          );
        } else {
          return roll + " + ";
        }
      }
    });
  };
  const total = rolls.reduce((a, b) => a + b);
  return <div>{determineDisplay()}</div>;
};

export default Roll;

