import React, { useState } from "react";

const Roll = (props) => {
  const [rollAmount, setRollAmount] = useState([])

  const [amount, sides, modifier] = props

  for (let i = 0; i < amount; i++){
    rollCalc(sides)
  }

  const rollCalc = (sides) => {
    return 1 + Math.floor(Math.random() * sides)
  }

  const addModifier = (roll, modifier) => {
    return roll + modifier;
  };

  const determineDisplay = () => {
    // If a modifier is applied, show the math involved
    if (props.modifier !== null){

      // crit fails are red
      if (props.roll === 1 && props.sides === 20){
      return <li style={{color:'red'}}>(1d{props.sides}): {props.roll} + {parseInt(props.modifier)} = {(props.roll + parseInt(props.modifier))}</li>
      }
      // crit hits are green
      if (props.roll === 20 && props.sides === 20){
        return <li style={{color:'green'}}>(1d{props.sides}): {props.roll} + {props.modifier} = {(props.roll + parseInt(props.modifier))}</li>
      } 
      // regular rolls are white
      else {
        return <li>(1d{props.sides}): {props.roll} + {props.modifier} = {(props.roll + parseInt(props.modifier))}</li>
      }

      // else if no modifier, just display dice roll
    } else {
      // crit fails are red
      if (props.roll === 1 && props.sides === 20){
      return <li style={{color:'red'}}>(1d{props.sides}): {props.roll}</li>
        }
        // crit hits are green
        if (props.roll === 20 && props.sides === 20){
          return <li style={{color:'green'}}>(1d{props.sides}): {props.roll}</li>
        } 
        // regular rolls are white
        else {
          return <li>(1d{props.sides}): {props.roll}</li>
        }
    }
  }


  return (
    <>
      {determineDisplay()}
    </>
  );
}

export default Roll;

