import React, {useState} from 'react'

const Roller = () => {
  const [rolls, setRolls] = useState([])
  const [modifier, setModifier] = useState(0)


// Rolls stored as objects with roll value and modifier value, if present
  const rollDice = (sides) => {
    console.log(sides)
    modifier !== 0 ?
    setRolls([...rolls, {roll: (1 + Math.floor(Math.random() * sides + modifier)), modifier: parseInt(modifier)}]) : setRolls([...rolls, {roll: (1 + Math.floor(Math.random() * sides)), modifier: null}])
    setModifier(0)
  }

  const addModifier = (roll, modifier) => {
    return roll + modifier
  }

  // const handleSidesChange = (event) => {
  //   setSides(event.target.value)
  // }

  const displayRolls = () => {
    return rolls.map(roll => {
      if (roll.modifier !== null){
        if (roll.roll === 1){
        return <p style={{color:'red'}}>{roll} + {parseInt(roll.modifier)} = {(roll.roll + roll.modifier)}</p>
        }
        if (roll.roll === 20){
          return <p style={{color:'green'}}>{roll.roll} + {roll.modifier} = {(roll.roll + roll.modifier)}</p>
        } 
        else {
          return <p>{roll.roll} + {roll.modifier} = {(roll.roll + roll.modifier)}</p>
        }
      } else {
        if (roll.roll === 1){
          return <p style={{color:'red'}}>{roll}}</p>
          }
          if (roll.roll === 20){
            return <p style={{color:'green'}}>{roll.roll}}</p>
          } 
          else {
            return <p>{roll.roll}</p>
          }
      }
    })
  }

  return(
    <div>
      <button onClick={() => rollDice(4)}>d4</button>
      <button onClick={() => rollDice(6)}>d6</button>
      <button onClick={() => rollDice(8)}>d8</button>
      <button onClick={() => rollDice(10)}>d10</button>
      <button onClick={() => rollDice(12)}>d12</button>
      <button onClick={() => rollDice(20)}>d20</button>
      <br></br>
      <input type='number' value={modifier} onChange={e => setModifier(e.target.value)}/>
      {displayRolls()}
    </div>
  )
}

export default Roller
