import React, {useState} from 'react'

const Roller = () => {
  const [roll, setRoll] = useState(0)
  const [sides, setSides] = useState(null)

  const rollDice = () => {
    setRoll(Math.floor(Math.random() * Math.floor(sides + 1)))
  }

  const handleSidesChange = (event) => {
    setSides(event.target.value)
  }

  return(
    <div>
      <input type='number' value={sides} onChange={event => setSides(event.target.value)}></input>
      <button type='submit' onClick={(event) => rollDice}>Roll 1d6!</button>
      <br></br>
      {roll}
    </div>
  )
}

export default Roller
