import React, {useState} from 'react'

const Roller = () => {
  const [rolls, setRolls] = useState([])
  const [sides, setSides] = useState(0)

  const rollDice = () => {
    console.log(sides)
    setRolls([...rolls, 1 + Math.floor(Math.random() * sides)])
  }

  const handleSidesChange = (event) => {
    setSides(event.target.value)
  }

  const displayRolls = () => {
    return rolls.map(roll => {
      if (roll === 1){
        return <p style={{color:'red'}}>{roll}</p>
      }
      if (roll === 20){
        return <p style={{color:'green'}}>{roll}</p>
      } 

      else {
        return <p>{roll}</p>
      }
    })
  }

  return(
    <div>
      <input type='number' value={sides} onChange={event => setSides(event.target.value)}></input>
      <button onClick={rollDice}>Roll!</button>
      <br></br>
      {displayRolls()}
    </div>
  )
}

export default Roller
