import React from 'react'

const Roll = (props) => {

  const determineDisplay = () => {
    if (props.modifier !== null){
      if (props.roll === 1){
      return <p style={{color:'red'}}>{props.roll} + {parseInt(props.modifier)} = {(props.roll + props.modifier)}</p>
      }
      if (props.roll === 20){
        return <p style={{color:'green'}}>{props.roll} + {props.modifier} = {(props.roll + props.modifier)}</p>
      } 
      else {
        return <p>{props.roll} + {props.modifier} = {(props.roll + props.modifier)}</p>
      }
    } else {
      if (props.roll === 1){
        return <p style={{color:'red'}}>{props.roll}</p>
        }
        if (props.roll === 20){
          return <p style={{color:'green'}}>{props.roll}</p>
        } 
        else {
          return <p>{props.roll}</p>
        }
    }
  }


  return (
    <div>
      {determineDisplay()}
    </div>
  );
}

export default Roll;

