import React, {useState} from 'react'

const Roller = () => {
  const [roll, setRoll] = useState(0)

  const rollDice = (sides) => {
    setRoll(Math.floor(Math.random() * Math.floor(sides + 1)))
  }

  return(
    <div>
      <input type='number'></input>
      <button type='submit'onClick={rollDice}>Roll 1d6!</button>
      {roll}
    </div>
  )
}

export default Roller
