import React, {useState} from 'react'

const Roller = () => {
  const [roll, setRoll] = useState(0)

  const rollDice = (event) => {
    console.log(event)
    setRoll(Math.floor(Math.random() * Math.floor(10)))
  }

  return(
    <div>
      <input type='number'></input>
      <button type='submit' onClick={(event) => rollDice(event)}>Roll 1d6!</button>
      <br></br>
      {roll}
    </div>
  )
}

export default Roller
